<?php

namespace ApiAlarmIntegration;

class Module extends \Modularity\Module
{
    public $slug         = 'alarms';
    public $nameSingular = 'Alarm';
    public $namePlural   = 'Alarms';
    public $description  = 'Outputs a list of alarms from Alarm API';
    public $icon         = 'data:image/svg+xml;base64,';
    public $supports     = array();
    public $plugin       = array();
    public $cacheTtl     = 3600 * 24 * 7; // Defaults to 7 days
    public $hideTitle    = false;
    public $isDeprecated = false;

    public $templateDir = APIALARMINTEGRATION_TEMPLATE_PATH;

    /**
     * Available "magic" methods for modules:
     * init()            What to do on initialization (if you must, use __construct with care,
     *                   this will probably break stuff!!)
     * data()            Use to send data to view (return array)
     * style()           Enqueue style only when module is used on page
     * script()          Enqueue script only when module is used on page
     * adminEnqueue()    Enqueue scripts for the module edit/add page in admin
     * template()        Return the view template (blade) the module should use when displayed
     */

    public function data(): array
    {
        $data['options'] = $this->getFields();

        if (
            !self::isApiError(
                $places = \ApiAlarmIntegration\Module::getPlaces(
                    trailingslashit(
                        $data['options']['api_url']
                    )
                )
            )
        ) {
            $data['places'] = $places;
        } else {
            $data['places'] = false;
        }

        $data['lang'] = (object) [
            'noResults'          => __("No results found on your query."),
            'communicationError' => __("Failed to get data, please try again later!", 'api-alarm-integration'),
            'loadMore'           => __("Load more", 'api-alarm-integration')
        ];

        return $data;
    }

    /**
     * Include CSS
     * @return void
     */
    public function style()
    {
        \ApiAlarmIntegration\App::enqueueStyle();
    }

    /**
     * Include scripts
     * @return void
     */
    public function script()
    {
        \ApiAlarmIntegration\App::enqueueAlarmScripts();
    }

    public static function getBaseUrl($sourceUrl)
    {
        $url = parse_url($sourceUrl);

        $port    = $url['port'] ? ':' . $url['port'] : '';
        $baseUrl = $url['scheme'] . '://' . $url['host'] . $port . '/';
        return $baseUrl;
    }

    public static function getPlacesUrl($sourceUrl, $fallbackUrl)
    {
        $url = parse_url($sourceUrl);

        // Fallback to default API URL if no path is provided
        if ($url === false || $url['path'] === '/') {
            $url = Module::getBaseUrl($fallbackUrl);
            return $url . 'json/wp/v2/place?per_page=100';
        }
        return $sourceUrl;
    }
    /**
     * Get all available places
     * @param  string $apiUrl
     * @return array
     */
    public static function getPlaces($apiUrl)
    {
        // Retrive from cache
        if ($places = wp_cache_get('ApiAlarmIntegrationPlaces')) {
            return $places;
        }
        $request = wp_remote_get($apiUrl, [
            'timeout' => 10,
        ]);
        $places  = json_decode(wp_remote_retrieve_body($request));

        //Cache result for 3 hours
        wp_cache_set('ApiAlarmIntegrationPlaces', $places, null, 60 * 60 * 3);

        return $places;
    }

    public static function isApiError($response)
    {
        if (is_object($response) && in_array($response->code, ['rest_no_route'])) {
            return true;
        }
        return false;
    }
}
